import { Component, OnInit } from '@angular/core';
import { BehaviorSubject, Observable, map } from 'rxjs';
import { CoinsEntity, Item } from '../../Core/Interfaces/crypto'
import { CoinGeckoService } from 'src/app/Core/Services/coin-gecko.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  constructor(private cryptoService: CoinGeckoService) {}

  cryptos!: Item[];
  crypto$! : Observable<Item[]>;
  c!: Item[];
  ngOnInit(){
    this.cryptoService.retrieveCrypto();
    this.cryptoService.data$.subscribe(data => this.c = data);
    this.cryptos = this.cryptoService.dataSubject$.value();
    console.log(this.cryptos)
  }
}
