import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { BehaviorSubject, map } from 'rxjs';
import { CoinsEntity, Item } from '../../Core/Interfaces/crypto'
import { CoinGeckoService } from 'src/app/Core/Services/coin-gecko.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  constructor(public http: HttpClient, private cryptoService: CoinGeckoService) {}

  cryptoState! : {};
  dataSubject$ = new BehaviorSubject(this.cryptoState)
  data$ = this.dataSubject$.asObservable();
  ngOnInit(){
    this.cryptoState = this.cryptoService.retrieveCrypto();
    this.dataSubject$.next(this.cryptoState);
    console.log(this.cryptoState);
  }
}
