import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { CoinsEntity, Crypto, Item } from '../Interfaces/crypto';
import { BehaviorSubject, map, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CoinGeckoService {
  //cryptoState! : CoinsEntity[] | null | undefined;
  dataSubject$ = new BehaviorSubject<CoinsEntity[]>([])
  data$ = this.dataSubject$.asObservable();

  constructor(public http: HttpClient) { }

  retrieveCrypto() :void {
    const r = this.http.get<Crypto>('https://localhost:3000/crypto')
    .pipe(
      tap((res) => {
        this.dataSubject$.next(res.coins);
        return res.coins;
      })
    );
    // return this.http.get<Crypto>('https://localhost:443/crypto');
  }
}
