import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { CoinsEntity, Crypto, Item } from '../Interfaces/crypto';
import { BehaviorSubject, map } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CoinGeckoService {
  cryptoState! : CoinsEntity[] | null | undefined;
  dataSubject$ = new BehaviorSubject(this.cryptoState)
  data$ = this.dataSubject$.asObservable();

  constructor(public http: HttpClient) { }

  retrieveCrypto() {
    return this.http.get<Crypto>('https://localhost:443/crypto').pipe(
      map((res) => {
        console.log("SERVFICE");
        this.cryptoState = res.coins;
        this.dataSubject$.next(this.cryptoState);
      })
    );
  }
}
