import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Item } from '../Interfaces/crypto';
import { BehaviorSubject, map } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CoinGeckoService {
  cryptoState! : Item[];
  dataSubject$ = new BehaviorSubject(this.cryptoState)
  data$ = this.dataSubject$.asObservable();

  constructor(private http: HttpClient) { }

  async retrieveCrypto() {
    return this.http.get<any>('https://localhost:8080/crypto').pipe(
      map((res) => {
        this.cryptoState = [...res];
        this.dataSubject$.next(this.cryptoState);
      })
    );
  }
}
